#!/usr/bin/env python3 """ Cross-platform WhatsApp Web Integration Test Script. Tests functionality across different platforms and verifies Windows enhancements exist. """
import sys import os import time import platform import inspect from pathlib import Path
# Add src to path sys.path.insert(0, str(Path(__file__).parent / "src"))
from multichannel_messaging.services.whatsapp_web_service import WhatsAppWebService from multichannel_messaging.core.models import Customer, MessageTemplate from multichannel_messaging.utils.logger import get_logger
logger = get_logger(__name__)
def test_service_initialization(): """Test that the service initializes correctly on all platforms.""" print("🚀 Testing Service Initialization") print("=" * 35)
try: service = WhatsAppWebService( close_existing_tabs=True, auto_send=False, rate_limit_per_minute=5, daily_message_limit=50, min_delay_seconds=30 )
print("✅ Service initialized successfully") print(f"📊 Close existing tabs: {service.close_existing_tabs}") print(f"📊 Auto-send: {service.auto_send}") print(f"📊 Rate limit: {service.rate_limit_per_minute}/min") print(f"📊 Daily limit: {service.daily_message_limit}")
return True
except Exception as e: print(f"❌ Service initialization failed: {e}") return False
def test_windows_methods_exist(): """Test that Windows-specific methods exist in the service.""" print("\n🪟 Testing Windows Methods Existence") print("=" * 40)
service = WhatsAppWebService()
# List of Windows-specific methods that should exist windows_methods = [ '_detect_chrome_windows', '_auto_send_javascript_windows', '_auto_send_windows', '_auto_send_windows_simple', '_close_whatsapp_tabs_windows', '_show_windows_notification' ]
results = {}
for method_name in windows_methods: exists = hasattr(service, method_name) results[method_name] = exists
if exists: method = getattr(service, method_name) is_callable = callable(method) print(f"✅ {method_name}: {'Callable' if is_callable else 'Not callable'}")
# Check if method has proper signature if is_callable: try: sig = inspect.signature(method) param_count = len(sig.parameters) print(f" 📋 Parameters: {param_count}") except Exception as e: print(f" ⚠️ Signature inspection failed: {e}") else: print(f"❌ {method_name}: Missing")
success_count = sum(1 for exists in results.values() if exists) total_count = len(results)
print(f"\n📊 Windows Methods: {success_count}/{total_count} found")
return success_count == total_count
def test_chrome_detection(): """Test Chrome detection on current platform.""" print("\n🔍 Testing Chrome Detection") print("=" * 28)
service = WhatsAppWebService()
try: chrome_available, chrome_info = service._check_chrome_availability()
print(f"🌐 Platform: {platform.system()}") print(f"🔍 Chrome Available: {chrome_available}") print(f"📋 Chrome Info: {chrome_info}")
# Test Windows-specific detection if on Windows if platform.system().lower() == "windows" and hasattr(service, '_detect_chrome_windows'): print("\n🪟 Testing Windows-specific Chrome detection...") try: chrome_details = service._detect_chrome_windows() print(f" Found: {chrome_details.get('found', False)}") print(f" Paths: {len(chrome_details.get('paths', []))}") print(f" Version: {chrome_details.get('version', 'Unknown')}") print(f" Registry: {chrome_details.get('registry_found', False)}") print(f" Running: {chrome_details.get('process_running', False)}") except Exception as e: print(f" ⚠️ Windows Chrome detection failed: {e}")
return True
except Exception as e: print(f"❌ Chrome detection failed: {e}") return False
def test_service_configuration(): """Test service configuration.""" print("\n⚙️ Testing Service Configuration") print("=" * 32)
