def test_message_processing(): """Test message processing functionality.""" print("\n📝 Testing Message Processing") print("=" * 30)
service = WhatsAppWebService()
try: # Configure service first service.configure_service(acknowledge_risks=True)
# Create test data customer = Customer( name="Test User", phone="+1234567890", email="test@example.com", company="Test Company" )
