except Exception as e: print(f"❌ Message processing test failed: {e}") import traceback traceback.print_exc() return False
def test_platform_specific_features(): """Test platform-specific features.""" print("\n🖥️ Testing Platform-Specific Features") print("=" * 38)
service = WhatsAppWebService() current_platform = platform.system().lower()
print(f"🖥️ Current Platform: {current_platform}")
# Test tab closing methods tab_closing_methods = { 'darwin': '_close_whatsapp_tabs_macos', 'windows': '_close_whatsapp_tabs_windows', 'linux': '_close_whatsapp_tabs_linux' }
expected_method = tab_closing_methods.get(current_platform) if expected_method: has_method = hasattr(service, expected_method) print(f"🧹 Tab Closing Method ({expected_method}): {'✅ Found' if has_method else '❌ Missing'}")
if has_method: try: # Test the method (should not fail even if no tabs to close) method = getattr(service, expected_method) result = method() print(f" 📊 Method Result: {result}") except Exception as e: print(f" ⚠️ Method execution failed: {e}")
# Test auto-send methods auto_send_methods = { 'darwin': ['_auto_send_macos', '_auto_send_macos_simple'], 'windows': ['_auto_send_windows', '_auto_send_windows_simple', '_auto_send_javascript_windows'], 'linux': ['_auto_send_linux'] }
expected_methods = auto_send_methods.get(current_platform, []) for method_name in expected_methods: has_method = hasattr(service, method_name) print(f"🤖 Auto-Send Method ({method_name}): {'✅ Found' if has_method else '❌ Missing'}")
return True
