# Chrome status chrome_status = service_info.get('chrome_status', {}) print(f"🌐 Chrome Available: {chrome_status.get('available', False)}") print(f"🌐 Chrome Info: {chrome_status.get('info', 'Unknown')}")
# Platform features platform_features = service_info.get('platform_features', []) print(f"🖥️ Platform Features: {len(platform_features)}") for feature in platform_features[:3]: # Show first 3 print(f" {feature}") if len(platform_features) > 3: print(f" ... and {len(platform_features) - 3} more")
# Warnings warnings = service_info.get('warnings', []) print(f"⚠️ Warnings: {len(warnings)}")
return True
except Exception as e: print(f"❌ Service info test failed: {e}") return False
def test_error_handling(): """Test error handling and edge cases.""" print("\n🛡️ Testing Error Handling") print("=" * 26)
service = WhatsAppWebService()
try: # Test with invalid phone number invalid_phone = service._format_phone_number("invalid") print(f"📞 Invalid Phone Handling: {invalid_phone}")
# Test with empty customer data try: customer = Customer( name="", phone="", email="", company="" ) print("❌ Empty customer should have failed validation") return False except Exception: print("✅ Empty customer properly rejected")
# Test service without configuration can_send, reason = service.can_send_message() print(f"🚫 Unconfigured Service: {can_send} - {reason}")
# Test last error functionality last_error = service.get_last_error() print(f"🔍 Last Error: {last_error}")
return True
except Exception as e: print(f"❌ Error handling test failed: {e}") return False
def run_performance_test(): """Test performance of key operations.""" print("\n⚡ Performance Testing") print("=" * 22)
service = WhatsAppWebService()
# Test service initialization time start_time = time.time() test_service = WhatsAppWebService() init_time = time.time() - start_time print(f"🚀 Service Initialization: {init_time:.3f}s")
# Test Chrome detection time start_time = time.time() chrome_available, chrome_info = service._check_chrome_availability() detection_time = time.time() - start_time print(f"🔍 Chrome Detection: {detection_time:.3f}s")
# Test service info generation time start_time = time.time() service_info = service.get_service_info() info_time = time.time() - start_time print(f"📊 Service Info Generation: {info_time:.3f}s")
# Performance thresholds thresholds = { 'init_time': 1.0, # Should initialize within 1 second 'detection_time': 2.0, # Chrome detection within 2 seconds 'info_time': 1.0 # Service info within 1 second }
performance_ok = ( init_time < thresholds['init_time'] and detection_time < thresholds['detection_time'] and info_time < thresholds['info_time'] )
print(f"⚡ Performance: {'✅ Good' if performance_ok else '⚠️ Slow'}")
return performance_ok
def main(): """Run all cross-platform tests.""" print("🌍 CSC-Reach WhatsApp Web Cross-Platform Test Suite") print("=" * 60)
print(f"🖥️ Platform: {platform.system()} {platform.release()}") print(f"🐍 Python: {platform.python_version()}") print(f"📁 Working Directory: {os.getcwd()}") print()
tests = [ ("Service Initialization", test_service_initialization), ("Windows Methods Existence", test_windows_methods_exist), ("Chrome Detection", test_chrome_detection), ("Service Configuration", test_service_configuration), ("Message Processing", test_message_processing), ("Platform-Specific Features", test_platform_specific_features), ("Service Information", test_service_info), ("Error Handling", test_error_handling), ("Performance", run_performance_test), ]
results = {}
try: for test_name, test_func in tests: print(f"\n{'='*60}") print(f"🧪 Running: {test_name}") print(f"{'='*60}")
try: result = test_func() results[test_name] = result status = "✅ PASSED" if result else "⚠️ PARTIAL" print(f"\n{status}: {test_name}") except Exception as e: results[test_name] = False print(f"\n❌ FAILED: {test_name} - {e}") import traceback traceback.print_exc()
# Summary print(f"\n{'='*60}") print("📊 TEST SUMMARY") print(f"{'='*60}")
passed = sum(1 for result in results.values() if result) total = len(results)
for test_name, result in results.items(): status = "✅" if result else "❌" print(f"{status} {test_name}")
print(f"\n🎯 Results: {passed}/{total} tests passed")
if passed == total: print("\n🎉 All tests passed! WhatsApp Web integration is working perfectly!") print("\n💡 Key Features Verified:") print(" • Service initialization and configuration") print(" • Cross-platform Chrome detection") print(" • Message processing and URL generation") print(" • Platform-specific method availability") print(" • Error handling and edge cases") print(" • Performance within acceptable limits")
if platform.system().lower() == "windows": print(" • Windows-specific enhancements active") else: print(" • Windows enhancements ready for Windows deployment")
elif passed > total * 0.8: print("\n✅ Most tests passed! System is largely functional.") print("💡 Minor issues detected but core functionality works") else: print("\n⚠️ Several tests failed. System needs attention.") print("💡 Check error messages above for specific issues")
except KeyboardInterrupt: print("\n\n⏹️ Test interrupted by user") except Exception as e: print(f"\n❌ Test suite failed: {e}") import traceback traceback.print_exc()
if __name__ == "__main__": main()

#!/usr/bin/env python3 """ Unit tests for Template Management System.
Tests the core functionality of the template management system including CRUD operations, categories, and data validation. """
import sys import tempfile import pytest from pathlib import Path from unittest.mock import Mock, patch
# Add src to path for testing sys.path.insert(0, str(Path(__file__).parent.parent.parent / "src"))
from multichannel_messaging.core.config_manager import ConfigManager from multichannel_messaging.core.template_manager import TemplateManager, TemplateCategory from multichannel_messaging.core.models import MessageTemplate, Customer from multichannel_messaging.utils.exceptions import ValidationError
class TestTemplateManager: """Test cases for TemplateManager class."""
@pytest.fixture def temp_config(self): """Create temporary config for testing.""" with tempfile.TemporaryDirectory() as temp_dir: temp_path = Path(temp_dir) config_manager = ConfigManager() # Override templates path for testing config_manager.get_templates_path = lambda: temp_path / "templates" yield config_manager
@pytest.fixture def template_manager(self, temp_config): """Create template manager with temporary config.""" return TemplateManager(temp_config)
@pytest.fixture def sample_template(self): """Create sample template for testing.""" return MessageTemplate( id="test_email", name="Test Email Template", channels=["email"], subject="Test Subject for {name}", content="Hello {name} from {company}! This is a test email.", variables=["name", "company"] )
@pytest.fixture def sample_customer(self): """Create sample customer for testing.""" return Customer( name="John Doe", company="Test Corp", phone="+1-555-0123", email="john@testcorp.com" )
def test_template_manager_initialization(self, template_manager): """Test template manager initializes correctly.""" assert template_manager is not None assert template_manager.templates_dir.exists()
# Should create default categories categories = template_manager.get_categories() assert len(categories) == 5
