i18n = get_i18n_manager()
# Test different languages for lang in ['en', 'es', 'pt']: i18n.set_language(lang) print(f"\n{lang.upper()}:") print(f" App Title: {i18n.translate('app_title')}") print(f" Send Messages: {i18n.translate('send_messages')}") print(f" Settings: {i18n.translate('settings')}")
def demo_pluralization(): """Demonstrate pluralization support.""" print("\n=== Pluralization Demo ===")
i18n = get_i18n_manager()
for lang in ['en', 'es', 'pt']: i18n.set_language(lang) print(f"\n{lang.upper()}:")
for count in [0, 1, 2, 5]: result = i18n.translate_plural('recipient_count', count) print(f" {result}")
