for lang in ['en', 'es', 'pt']: i18n.set_language(lang) print(f"\n{lang.upper()}:")
# Same key, different contexts button_save = i18n.translate_context('save', 'button') menu_save = i18n.translate_context('save', 'menu')
print(f" Button Save: {button_save}") print(f" Menu Save: {menu_save}")
