i18n = get_i18n_manager() formatter = get_locale_formatter()
test_date = date(2024, 3, 15) test_number = 1234.56 test_currency = 1234.56 test_phone = "1234567890"
for lang in ['en', 'es', 'pt']: i18n.set_language(lang) print(f"\n{lang.upper()}:")
print(f" Date: {formatter.format_date(test_date)}") print(f" Number: {formatter.format_number(test_number)}") print(f" Currency: {formatter.format_currency(test_currency)}") print(f" Phone: {formatter.format_phone(test_phone)}") print(f" Percentage: {formatter.format_percentage(0.75)}") print(f" File Size: {formatter.format_file_size(1572864)}") # 1.5 MB
