print(f"Translation Report Summary:") print(f" Total Languages: {report['summary']['total_languages']}") print(f" Total Keys: {report['summary']['total_keys']}") print(f" Overall Completion: {report['summary']['overall_completion']:.1f}%")
print(f"\nLanguage Details:") for lang_code, lang_data in report['languages'].items(): print(f" {lang_code} ({lang_data['name']}):") print(f" Completion: {lang_data['completion_percentage']:.1f}%") print(f" Missing Keys: {lang_data['missing_keys']}") print(f" Validation Issues: {lang_data['issue_count']}")
def demo_dynamic_switching(): """Demonstrate dynamic language switching with callbacks.""" print("\n=== Dynamic Language Switching Demo ===")
i18n = get_i18n_manager()
# Register callback def language_change_callback(new_lang): print(f" Language changed to: {new_lang}")
i18n.register_language_change_callback(language_change_callback)
print("Switching languages (with callback):") for lang in ['en', 'es', 'pt', 'en']: i18n.set_language(lang) print(f" Current: {i18n.translate('language')} ({lang})")
# Unregister callback i18n.unregister_language_change_callback(language_change_callback)
def demo_error_handling(): """Demonstrate error handling.""" print("\n=== Error Handling Demo ===")
i18n = get_i18n_manager() formatter = get_locale_formatter()
