# Test invalid formatting print(f" Invalid date: '{formatter.format_date('invalid')}'") print(f" Invalid number: '{formatter.format_number('not_a_number')}'")
def main(): """Run all demonstrations.""" print("CSC-Reach Enhanced I18n System Demonstration") print("=" * 50)
try: demo_basic_translations() demo_pluralization() demo_context_aware() demo_locale_formatting() demo_rtl_support() demo_translation_management() demo_dynamic_switching() demo_error_handling()
print("\n" + "=" * 50) print("Demonstration completed successfully!")
except Exception as e: print(f"\nError during demonstration: {e}") import traceback traceback.print_exc()
if __name__ == '__main__': main()

