""" User preferences management for CSC-Reach application. Handles customizable interface settings and user preferences. """
import json from pathlib import Path from typing import Dict, Any, Optional, List from dataclasses import dataclass, field, asdict from enum import Enum from PySide6.QtCore import QObject, Signal from PySide6.QtWidgets import QApplication from PySide6.QtGui import QKeySequence
from ..utils.logger import get_logger from ..utils.platform_utils import get_config_dir
logger = get_logger(__name__)
class ToolbarPosition(Enum): """Toolbar position options.""" TOP = "top" BOTTOM = "bottom" LEFT = "left" RIGHT = "right"
class WindowLayout(Enum): """Window layout options.""" STANDARD = "standard" COMPACT = "compact" WIDE = "wide" MINIMAL = "minimal"
@dataclass class KeyboardShortcut: """Keyboard shortcut configuration.""" action: str sequence: str description: str category: str = "general"
def to_qkeysequence(self) -> QKeySequence: """Convert to QKeySequence.""" return QKeySequence(self.sequence)
@dataclass class ToolbarConfig: """Toolbar configuration.""" position: ToolbarPosition = ToolbarPosition.TOP visible: bool = True icon_size: int = 24 show_text: bool = True buttons: List[str] = field(default_factory=lambda: [ "import_csv", "send_messages", "create_draft", "template_library", "settings" ])
@dataclass class WindowConfig: """Window configuration.""" layout: WindowLayout = WindowLayout.STANDARD remember_geometry: bool = True remember_splitter_state: bool = True width: int = 1200 height: int = 800 x: int = -1 y: int = -1 maximized: bool = False splitter_sizes: List[int] = field(default_factory=lambda: [400, 800])
@dataclass class InterfaceConfig: """Interface configuration.""" theme: str = "system" font_family: str = "" font_size: int = 9 high_contrast: bool = False animations_enabled: bool = True show_tooltips: bool = True compact_mode: bool = False show_status_bar: bool = True show_progress_details: bool = True
@dataclass class AccessibilityConfig: """Accessibility configuration.""" screen_reader_support: bool = False high_contrast_mode: bool = False large_fonts: bool = False keyboard_navigation_only: bool = False voice_control_enabled: bool = False focus_indicators_enhanced: bool = False
@dataclass class UserPreferences: """Complete user preferences.""" version: str = "1.0" toolbar: ToolbarConfig = field(default_factory=ToolbarConfig) window: WindowConfig = field(default_factory=WindowConfig) interface: InterfaceConfig = field(default_factory=InterfaceConfig) accessibility: AccessibilityConfig = field(default_factory=AccessibilityConfig) keyboard_shortcuts: List[KeyboardShortcut] = field(default_factory=list) custom_settings: Dict[str, Any] = field(default_factory=dict)
class UserPreferencesManager(QObject): """Manages user preferences and customization settings."""
# Signals preferences_changed = Signal(str) # preference_category theme_changed = Signal(str) # theme_name layout_changed = Signal(str) # layout_name shortcuts_changed = Signal()
def __init__(self, config_manager=None): super().__init__() self.config_manager = config_manager self.config_dir = get_config_dir() self.preferences_file = self.config_dir / "user_preferences.json"
# Default preferences self.preferences = UserPreferences()
# Load preferences self.load_preferences()
# Initialize default shortcuts if not self.preferences.keyboard_shortcuts: self._initialize_default_shortcuts()
def load_preferences(self): """Load preferences from file.""" try: if self.preferences_file.exists(): with open(self.preferences_file, 'r', encoding='utf-8') as f: data = json.load(f)
# Convert dict back to dataclass self.preferences = self._dict_to_preferences(data) logger.info("Loaded user preferences") else: # Create default preferences file self.save_preferences() logger.info("Created default user preferences")
except Exception as e: logger.error(f"Failed to load user preferences: {e}") # Use defaults self.preferences = UserPreferences()
def save_preferences(self): """Save preferences to file.""" try: self.config_dir.mkdir(parents=True, exist_ok=True)
# Convert dataclass to dict data = self._preferences_to_dict(self.preferences)
with open(self.preferences_file, 'w', encoding='utf-8') as f: json.dump(data, f, indent=2)
logger.debug("Saved user preferences")
except Exception as e: logger.error(f"Failed to save user preferences: {e}")
def _preferences_to_dict(self, prefs: UserPreferences) -> Dict[str, Any]: """Convert preferences dataclass to dictionary.""" data = asdict(prefs)
# Convert enums to strings data['toolbar']['position'] = prefs.toolbar.position.value data['window']['layout'] = prefs.window.layout.value
return data
def _dict_to_preferences(self, data: Dict[str, Any]) -> UserPreferences: """Convert dictionary to preferences dataclass.""" # Handle toolbar config toolbar_data = data.get('toolbar', {}) toolbar = ToolbarConfig( position=ToolbarPosition(toolbar_data.get('position', 'top')), visible=toolbar_data.get('visible', True), icon_size=toolbar_data.get('icon_size', 24), show_text=toolbar_data.get('show_text', True), buttons=toolbar_data.get('buttons', ToolbarConfig().buttons) )
