# Handle interface config interface_data = data.get('interface', {}) interface = InterfaceConfig( theme=interface_data.get('theme', 'system'), font_family=interface_data.get('font_family', ''), font_size=interface_data.get('font_size', 9), high_contrast=interface_data.get('high_contrast', False), animations_enabled=interface_data.get('animations_enabled', True), show_tooltips=interface_data.get('show_tooltips', True), compact_mode=interface_data.get('compact_mode', False), show_status_bar=interface_data.get('show_status_bar', True), show_progress_details=interface_data.get('show_progress_details', True) )
# Handle accessibility config accessibility_data = data.get('accessibility', {}) accessibility = AccessibilityConfig( screen_reader_support=accessibility_data.get('screen_reader_support', False), high_contrast_mode=accessibility_data.get('high_contrast_mode', False), large_fonts=accessibility_data.get('large_fonts', False), keyboard_navigation_only=accessibility_data.get('keyboard_navigation_only', False), voice_control_enabled=accessibility_data.get('voice_control_enabled', False), focus_indicators_enhanced=accessibility_data.get('focus_indicators_enhanced', False) )
# Handle keyboard shortcuts shortcuts_data = data.get('keyboard_shortcuts', []) shortcuts = [] for shortcut_data in shortcuts_data: shortcuts.append(KeyboardShortcut( action=shortcut_data['action'], sequence=shortcut_data['sequence'], description=shortcut_data['description'], category=shortcut_data.get('category', 'general') ))
return UserPreferences( version=data.get('version', '1.0'), toolbar=toolbar, window=window, interface=interface, accessibility=accessibility, keyboard_shortcuts=shortcuts, custom_settings=data.get('custom_settings', {}) )
