self.preferences.keyboard_shortcuts = default_shortcuts self.save_preferences()
# Getter methods def get_toolbar_config(self) -> ToolbarConfig: """Get toolbar configuration.""" return self.preferences.toolbar
def get_window_config(self) -> WindowConfig: """Get window configuration.""" return self.preferences.window
