# Utility methods def export_preferences(self, file_path: Path): """Export preferences to file.""" try: data = self._preferences_to_dict(self.preferences) with open(file_path, 'w', encoding='utf-8') as f: json.dump(data, f, indent=2) logger.info(f"Exported preferences to {file_path}") except Exception as e: logger.error(f"Failed to export preferences: {e}") raise
