    def get_theme_options(self) -> Dict[str, str]:
        """Get available theme options for UI."""
        return {
            "system": "System Default",
            "light": "Light Mode", 
            "dark": "Dark Mode"
        }

    def is_dark_theme(self) -> bool:
        """Check if current theme is dark."""
        if self.current_theme == ThemeMode.DARK:
            return True
        elif self.current_theme == ThemeMode.SYSTEM:
            # Try to detect system theme
            app = QApplication.instance()
            if app:
                palette = app.palette()
                window_color = palette.color(QPalette.Window)
                # Consider dark if window background is darker
                return window_color.lightness() < 128
        return False

    def apply_button_style(self, button, style_class: str = "primary"):
        """Apply specific style class to a button."""
        if style_class == "secondary":
            button.setProperty("class", "secondary")
        elif style_class == "success":
            button.setProperty("class", "success")
        elif style_class == "danger":
            button.setProperty("class", "danger")

        # Refresh style
        button.style().unpolish(button)
        button.style().polish(button)

"""
Custom exception classes for Multi-Channel Bulk Messaging System.
"""


class MultiChannelMessagingError(Exception):
    """Base exception class for the application."""
    pass


class ConfigurationError(MultiChannelMessagingError):
    """Raised when there's a configuration error."""
    pass


class ValidationError(MultiChannelMessagingError):
    """Raised when data validation fails."""
    pass


class CSVProcessingError(MultiChannelMessagingError):
    """Raised when CSV processing fails."""
    pass


class EmailServiceError(MultiChannelMessagingError):
    """Raised when email service operations fail."""
    pass


class OutlookIntegrationError(EmailServiceError):
    """Raised when Outlook integration fails."""
    pass


class QuotaExceededError(MultiChannelMessagingError):
    """Raised when daily quota is exceeded."""
    pass


class TemplateError(MultiChannelMessagingError):
    """Raised when template processing fails."""
    pass


class ServiceUnavailableError(MultiChannelMessagingError):
    """Raised when a required service is unavailable."""
    pass


class WhatsAppAPIError(ServiceUnavailableError):
    """Raised when WhatsApp Business API operations fail."""
    pass


class WhatsAppConfigurationError(ConfigurationError):
    """Raised when WhatsApp configuration is invalid or missing."""
    pass

""" Language settings dialog for CSC-Reach internationalization. """
from PySide6.QtWidgets import ( QDialog, QVBoxLayout, QHBoxLayout, QGridLayout, QLabel, QPushButton, QComboBox, QMessageBox, QGroupBox ) from PySide6.QtCore import Qt, Signal from PySide6.QtGui import QFont
from ..core.i18n_manager import get_i18n_manager, tr from ..utils.logger import get_logger
logger = get_logger(__name__)
class LanguageSettingsDialog(QDialog): """ Dialog for selecting application language. """
language_changed = Signal(str) # Emitted when language changes
def __init__(self, parent=None): super().__init__(parent) self.i18n_manager = get_i18n_manager() self.setup_ui() self.load_current_settings()
def setup_ui(self): """Set up the user interface.""" self.setWindowTitle(tr("language_settings")) self.setMinimumSize(400, 200) self.setModal(True)
# Main layout layout = QVBoxLayout(self)
# Language selection section self.create_language_section(layout)
# Information section self.create_info_section(layout)
# Button section self.create_button_section(layout)
def create_language_section(self, layout): """Create the language selection section.""" lang_group = QGroupBox(tr("language")) lang_layout = QGridLayout(lang_group)
# Language selection lang_layout.addWidget(QLabel(tr("select_language")), 0, 0)
self.language_combo = QComboBox() self.populate_language_combo() lang_layout.addWidget(self.language_combo, 0, 1)
layout.addWidget(lang_group)
def create_info_section(self, layout): """Create the information section.""" info_group = QGroupBox(tr("dialog.information")) info_layout = QVBoxLayout(info_group)
info_text = QLabel( f"• {tr('language_restart_info')}\n" f"• {tr('language_interface_info')}\n" f"• {tr('language_templates_info')}" ) info_text.setWordWrap(True) info_text.setStyleSheet("color: #666; font-style: italic;") info_layout.addWidget(info_text)
layout.addWidget(info_group)
def create_button_section(self, layout): """Create the button section.""" button_layout = QHBoxLayout()
button_layout.addStretch()
# Cancel button cancel_btn = QPushButton(tr("cancel")) cancel_btn.clicked.connect(self.reject) button_layout.addWidget(cancel_btn)
# Save button save_btn = QPushButton(tr("save")) save_btn.clicked.connect(self.save_language) save_btn.setDefault(True) button_layout.addWidget(save_btn)
layout.addLayout(button_layout)
def populate_language_combo(self): """Populate the language combo box.""" supported_languages = self.i18n_manager.get_supported_languages()
for lang_code, lang_info in supported_languages.items(): display_name = f"{lang_info['native']} ({lang_info['name']})" self.language_combo.addItem(display_name, lang_code)
def load_current_settings(self): """Load current language settings.""" current_lang = self.i18n_manager.get_current_language()
# Find and select current language in combo for i in range(self.language_combo.count()): if self.language_combo.itemData(i) == current_lang: self.language_combo.setCurrentIndex(i) break
def save_language(self): """Save the selected language.""" try: # Get selected language selected_lang = self.language_combo.currentData() current_lang = self.i18n_manager.get_current_language()
if selected_lang == current_lang: # No change self.accept() return
# Set new language success = self.i18n_manager.set_language(selected_lang)
if success: # Emit signal self.language_changed.emit(selected_lang)
# Show restart message lang_info = self.i18n_manager.get_supported_languages()[selected_lang] QMessageBox.information( self, tr("language_changed"), tr("language_changed_message", language=lang_info['native']) )
self.accept() else: QMessageBox.critical( self, tr("error"), tr("language_change_failed") )
except Exception as e: logger.error(f"Failed to save language: {e}") QMessageBox.critical( self, tr("error"), tr("language_change_error", error=str(e)) )

"""
Message preview dialog for CSC-Reach.
Shows formatted preview of messages with proper sizing.
"""

from PySide6.QtWidgets import (
    QDialog, QVBoxLayout, QHBoxLayout, QTextEdit, QPushButton, 
    QLabel, QScrollArea, QWidget, QFrame
)
from PySide6.QtCore import Qt
from PySide6.QtGui import QFont

from ..core.i18n_manager import tr


class PreviewDialog(QDialog):
    """
    Dialog for previewing messages with proper formatting and sizing.
    """

    def __init__(self, customer_name: str, preview_content: str, parent=None):
        super().__init__(parent)
        self.customer_name = customer_name
        self.preview_content = preview_content
        self.setup_ui()

    def setup_ui(self):
        """Set up the user interface."""
        self.setWindowTitle(tr("preview_message"))
        self.setMinimumSize(600, 500)
        self.resize(700, 600)
        self.setModal(True)

        # Main layout
        layout = QVBoxLayout(self)

        # Header
        header_label = QLabel(f"Preview for: {self.customer_name}")
        header_label.setStyleSheet("""
            QLabel {
                font-size: 14px;
                font-weight: bold;
                color: #2c3e50;
                padding: 10px;
                background-color: #ecf0f1;
                border-radius: 5px;
                margin-bottom: 10px;
            }
        """)
        layout.addWidget(header_label)

        # Preview content area
        self.preview_text = QTextEdit()
        self.preview_text.setPlainText(self.preview_content)
        self.preview_text.setReadOnly(True)

        # Set font for better readability
        font = QFont("Consolas", 10)
        if not font.exactMatch():
            font = QFont("Monaco", 10)
        if not font.exactMatch():
            font = QFont("Courier New", 10)
        self.preview_text.setFont(font)

        # Style the preview area with explicit text color
        self.preview_text.setStyleSheet("""
            QTextEdit {
                background-color: #ffffff;
                color: #2c3e50;
                border: 1px solid #dee2e6;
                border-radius: 5px;
                padding: 10px;
                line-height: 1.4;
                selection-background-color: #3498db;
                selection-color: #ffffff;
            }
        """)

        layout.addWidget(self.preview_text)

        # Button area
        button_layout = QHBoxLayout()
        button_layout.addStretch()

        close_btn = QPushButton(tr("close"))
        close_btn.clicked.connect(self.accept)
        close_btn.setDefault(True)
        close_btn.setMinimumWidth(100)
        button_layout.addWidget(close_btn)

        layout.addLayout(button_layout)

    def set_preview_content(self, content: str):
        """Update the preview content."""
        self.preview_content = content
        self.preview_text.setPlainText(content)

